{
  "name": "08-Planka-to-NocoDB-Sync",
  "nodes": [
    {
      "parameters": {},
      "id": "start",
      "name": "Start",
      "type": "n8n-nodes-base.start",
      "typeVersion": 1,
      "position": [240, 300]
    },
    {
      "parameters": {
        "httpMethod": "POST",
        "path": "planka-webhook",
        "options": {}
      },
      "id": "webhook",
      "name": "Planka Webhook",
      "type": "n8n-nodes-base.webhook",
      "typeVersion": 1.1,
      "position": [240, 480],
      "webhookId": "planka-card-update"
    },
    {
      "parameters": {
        "conditions": {
          "string": [
            {
              "value1": "={{ $json.action }}",
              "operation": "equals",
              "value2": "card:update"
            }
          ]
        }
      },
      "id": "filter-updates",
      "name": "Filter Card Updates",
      "type": "n8n-nodes-base.filter",
      "typeVersion": 1,
      "position": [460, 400]
    },
    {
      "parameters": {
        "jsCode": "// Extract card data from Planka webhook\nconst card = $input.first().json.card;\nconst list = $input.first().json.list;\n\n// Map Planka list to NocoDB status\nconst statusMap = {\n  'To Do': 'To Do',\n  'In Progress': 'In Progress',\n  'Review': 'Review',\n  'Done': 'Complete'\n};\n\nreturn [{\n  json: {\n    planka_card_id: card.id,\n    title: card.name,\n    description: card.description,\n    status: statusMap[list.name] || 'To Do',\n    due_date: card.dueDate,\n    updated_at: new Date().toISOString()\n  }\n}];"
      },
      "id": "transform-data",
      "name": "Transform Card Data",
      "type": "n8n-nodes-base.code",
      "typeVersion": 2,
      "position": [660, 400]
    },
    {
      "parameters": {
        "url": "=http://nocodb:8080/api/v2/tables/Tasks/records",
        "authentication": "genericCredentialType",
        "genericAuthType": "httpHeaderAuth",
        "options": {
          "qs": {
            "parameter": [
              {
                "name": "where",
                "value": "=(planka_card_id,eq,{{ $json.planka_card_id }})"
              }
            ]
          }
        }
      },
      "id": "nocodb-find-task",
      "name": "Find NocoDB Task",
      "type": "n8n-nodes-base.httpRequest",
      "typeVersion": 4.1,
      "position": [860, 400],
      "credentials": {
        "httpHeaderAuth": {
          "id": "nocodb-api",
          "name": "NocoDB API"
        }
      }
    },
    {
      "parameters": {
        "conditions": {
          "number": [
            {
              "value1": "={{ $json.list?.length }}",
              "operation": "larger",
              "value2": 0
            }
          ]
        }
      },
      "id": "check-exists",
      "name": "Task Exists?",
      "type": "n8n-nodes-base.if",
      "typeVersion": 1,
      "position": [1060, 400]
    },
    {
      "parameters": {
        "method": "PATCH",
        "url": "=http://nocodb:8080/api/v2/tables/Tasks/records/{{ $node['Find NocoDB Task'].json.list[0].Id }}",
        "authentication": "genericCredentialType",
        "genericAuthType": "httpHeaderAuth",
        "sendBody": true,
        "specifyBody": "json",
        "jsonBody": "={\n  \"Title\": \"{{ $node['Transform Card Data'].json.title }}\",\n  \"Status\": \"{{ $node['Transform Card Data'].json.status }}\",\n  \"Description\": \"{{ $node['Transform Card Data'].json.description }}\",\n  \"synced_at\": \"{{ $now.toISO() }}\"\n}",
        "options": {}
      },
      "id": "nocodb-update-task",
      "name": "Update NocoDB Task",
      "type": "n8n-nodes-base.httpRequest",
      "typeVersion": 4.1,
      "position": [1280, 300],
      "credentials": {
        "httpHeaderAuth": {
          "id": "nocodb-api",
          "name": "NocoDB API"
        }
      }
    },
    {
      "parameters": {
        "method": "POST",
        "url": "http://nocodb:8080/api/v2/tables/Tasks/records",
        "authentication": "genericCredentialType",
        "genericAuthType": "httpHeaderAuth",
        "sendBody": true,
        "specifyBody": "json",
        "jsonBody": "={\n  \"Title\": \"{{ $node['Transform Card Data'].json.title }}\",\n  \"Description\": \"{{ $node['Transform Card Data'].json.description }}\",\n  \"Status\": \"{{ $node['Transform Card Data'].json.status }}\",\n  \"Priority\": \"Medium\",\n  \"planka_card_id\": \"{{ $node['Transform Card Data'].json.planka_card_id }}\",\n  \"synced_at\": \"{{ $now.toISO() }}\"\n}",
        "options": {}
      },
      "id": "nocodb-create-task",
      "name": "Create NocoDB Task",
      "type": "n8n-nodes-base.httpRequest",
      "typeVersion": 4.1,
      "position": [1280, 500],
      "credentials": {
        "httpHeaderAuth": {
          "id": "nocodb-api",
          "name": "NocoDB API"
        }
      }
    },
    {
      "parameters": {
        "url": "http://ntfy:80/task-sync",
        "method": "POST",
        "sendBody": true,
        "specifyBody": "string",
        "body": "=Planka card \"{{ $node['Transform Card Data'].json.title }}\" synced to NocoDB",
        "options": {
          "headers": {
            "parameters": [
              {
                "name": "Title",
                "value": "Planka Sync"
              },
              {
                "name": "Tags",
                "value": "arrows_counterclockwise"
              }
            ]
          }
        }
      },
      "id": "ntfy-notify",
      "name": "Notify Sync",
      "type": "n8n-nodes-base.httpRequest",
      "typeVersion": 4.1,
      "position": [1500, 400]
    }
  ],
  "connections": {
    "Start": {
      "main": [
        [
          {
            "node": "Filter Card Updates",
            "type": "main",
            "index": 0
          }
        ]
      ]
    },
    "Planka Webhook": {
      "main": [
        [
          {
            "node": "Filter Card Updates",
            "type": "main",
            "index": 0
          }
        ]
      ]
    },
    "Filter Card Updates": {
      "main": [
        [
          {
            "node": "Transform Card Data",
            "type": "main",
            "index": 0
          }
        ]
      ]
    },
    "Transform Card Data": {
      "main": [
        [
          {
            "node": "Find NocoDB Task",
            "type": "main",
            "index": 0
          }
        ]
      ]
    },
    "Find NocoDB Task": {
      "main": [
        [
          {
            "node": "Task Exists?",
            "type": "main",
            "index": 0
          }
        ]
      ]
    },
    "Task Exists?": {
      "main": [
        [
          {
            "node": "Update NocoDB Task",
            "type": "main",
            "index": 0
          }
        ],
        [
          {
            "node": "Create NocoDB Task",
            "type": "main",
            "index": 0
          }
        ]
      ]
    },
    "Update NocoDB Task": {
      "main": [
        [
          {
            "node": "Notify Sync",
            "type": "main",
            "index": 0
          }
        ]
      ]
    },
    "Create NocoDB Task": {
      "main": [
        [
          {
            "node": "Notify Sync",
            "type": "main",
            "index": 0
          }
        ]
      ]
    }
  },
  "settings": {
    "executionOrder": "v1"
  },
  "staticData": null,
  "tags": [
    {
      "name": "sync",
      "createdAt": "2026-01-31T00:00:00.000Z",
      "updatedAt": "2026-01-31T00:00:00.000Z"
    }
  ],
  "triggerCount": 1,
  "meta": {
    "templateCredsSetupCompleted": false,
    "instanceId": "planka-nocodb-sync"
  }
}

{
  "name": "09-Unified-Notifications",
  "nodes": [
    {
      "parameters": {},
      "id": "start",
      "name": "Start",
      "type": "n8n-nodes-base.start",
      "typeVersion": 1,
      "position": [240, 300]
    },
    {
      "parameters": {
        "httpMethod": "POST",
        "path": "notify",
        "options": {
          "responseMode": "responseNode"
        }
      },
      "id": "webhook-notify",
      "name": "Notification Webhook",
      "type": "n8n-nodes-base.webhook",
      "typeVersion": 1.1,
      "position": [240, 480],
      "webhookId": "unified-notify"
    },
    {
      "parameters": {
        "jsCode": "// Unified notification processor\n// Accepts: { type, title, message, priority, topic, tags }\n\nconst input = $input.first().json;\n\n// Default values\nconst notification = {\n  type: input.type || 'info',\n  title: input.title || 'Notification',\n  message: input.message || '',\n  priority: input.priority || 3,\n  topic: input.topic || 'system-alerts',\n  tags: input.tags || []\n};\n\n// Map type to emoji tags\nconst typeEmojis = {\n  'success': 'white_check_mark',\n  'error': 'x',\n  'warning': 'warning',\n  'info': 'information_source',\n  'task': 'clipboard',\n  'sync': 'arrows_counterclockwise',\n  'report': 'bar_chart'\n};\n\nif (typeEmojis[notification.type]) {\n  notification.tags.push(typeEmojis[notification.type]);\n}\n\n// Map priority to ntfy priority\nconst priorityMap = {\n  1: 'min',\n  2: 'low', \n  3: 'default',\n  4: 'high',\n  5: 'urgent'\n};\n\nnotification.ntfy_priority = priorityMap[notification.priority] || 'default';\n\nreturn [{ json: notification }];"
      },
      "id": "process-notification",
      "name": "Process Notification",
      "type": "n8n-nodes-base.code",
      "typeVersion": 2,
      "position": [460, 400]
    },
    {
      "parameters": {
        "url": "=http://ntfy:80/{{ $json.topic }}",
        "method": "POST",
        "sendBody": true,
        "specifyBody": "string",
        "body": "={{ $json.message }}",
        "options": {
          "headers": {
            "parameters": [
              {
                "name": "Title",
                "value": "={{ $json.title }}"
              },
              {
                "name": "Priority",
                "value": "={{ $json.ntfy_priority }}"
              },
              {
                "name": "Tags",
                "value": "={{ $json.tags.join(',') }}"
              }
            ]
          }
        }
      },
      "id": "send-ntfy",
      "name": "Send to Ntfy",
      "type": "n8n-nodes-base.httpRequest",
      "typeVersion": 4.1,
      "position": [680, 400]
    },
    {
      "parameters": {
        "conditions": {
          "number": [
            {
              "value1": "={{ $node['Process Notification'].json.priority }}",
              "operation": "largerEqual",
              "value2": 4
            }
          ]
        }
      },
      "id": "check-high-priority",
      "name": "High Priority?",
      "type": "n8n-nodes-base.if",
      "typeVersion": 1,
      "position": [680, 580]
    },
    {
      "parameters": {
        "method": "POST",
        "url": "http://nocodb:8080/api/v2/tables/NotificationLog/records",
        "authentication": "genericCredentialType",
        "genericAuthType": "httpHeaderAuth",
        "sendBody": true,
        "specifyBody": "json",
        "jsonBody": "={\n  \"Type\": \"{{ $node['Process Notification'].json.type }}\",\n  \"Title\": \"{{ $node['Process Notification'].json.title }}\",\n  \"Message\": \"{{ $node['Process Notification'].json.message }}\",\n  \"Priority\": {{ $node['Process Notification'].json.priority }},\n  \"Topic\": \"{{ $node['Process Notification'].json.topic }}\",\n  \"SentAt\": \"{{ $now.toISO() }}\",\n  \"Acknowledged\": false\n}",
        "options": {}
      },
      "id": "log-to-nocodb",
      "name": "Log to NocoDB",
      "type": "n8n-nodes-base.httpRequest",
      "typeVersion": 4.1,
      "position": [900, 580],
      "credentials": {
        "httpHeaderAuth": {
          "id": "nocodb-api",
          "name": "NocoDB API"
        }
      }
    },
    {
      "parameters": {
        "respondWith": "json",
        "responseBody": "={ \"success\": true, \"notification_id\": \"{{ $json.id || 'sent' }}\" }"
      },
      "id": "respond-success",
      "name": "Respond Success",
      "type": "n8n-nodes-base.respondToWebhook",
      "typeVersion": 1,
      "position": [900, 400]
    }
  ],
  "connections": {
    "Start": {
      "main": [
        [
          {
            "node": "Process Notification",
            "type": "main",
            "index": 0
          }
        ]
      ]
    },
    "Notification Webhook": {
      "main": [
        [
          {
            "node": "Process Notification",
            "type": "main",
            "index": 0
          }
        ]
      ]
    },
    "Process Notification": {
      "main": [
        [
          {
            "node": "Send to Ntfy",
            "type": "main",
            "index": 0
          },
          {
            "node": "Check High Priority?",
            "type": "main",
            "index": 0
          }
        ]
      ]
    },
    "Send to Ntfy": {
      "main": [
        [
          {
            "node": "Respond Success",
            "type": "main",
            "index": 0
          }
        ]
      ]
    },
    "Check High Priority?": {
      "main": [
        [
          {
            "node": "Log to NocoDB",
            "type": "main",
            "index": 0
          }
        ],
        []
      ]
    }
  },
  "settings": {
    "executionOrder": "v1"
  },
  "staticData": null,
  "tags": [
    {
      "name": "notifications",
      "createdAt": "2026-01-31T00:00:00.000Z",
      "updatedAt": "2026-01-31T00:00:00.000Z"
    }
  ],
  "triggerCount": 1,
  "meta": {
