{
  "name": "10-Daily-Summary-Report",
  "nodes": [
    {
      "parameters": {},
      "id": "start",
      "name": "Start",
      "type": "n8n-nodes-base.start",
      "typeVersion": 1,
      "position": [240, 300]
    },
    {
      "parameters": {
        "rule": {
          "interval": [
            {
              "field": "cronExpression",
              "expression": "0 18 * * *"
            }
          ]
        }
      },
      "id": "schedule-daily",
      "name": "Daily at 6 PM",
      "type": "n8n-nodes-base.scheduleTrigger",
      "typeVersion": 1.1,
      "position": [240, 480]
    },
    {
      "parameters": {
        "url": "http://nocodb:8080/api/v2/tables/Tasks/records",
        "authentication": "genericCredentialType",
        "genericAuthType": "httpHeaderAuth",
        "options": {
          "qs": {
            "parameter": [
              {
                "name": "where",
                "value": "(Status,eq,Complete)~and(updated_at,gte,today)"
              }
            ]
          }
        }
      },
      "id": "get-completed-today",
      "name": "Get Completed Today",
      "type": "n8n-nodes-base.httpRequest",
      "typeVersion": 4.1,
      "position": [460, 300],
      "credentials": {
        "httpHeaderAuth": {
          "id": "nocodb-api",
          "name": "NocoDB API"
        }
      }
    },
    {
      "parameters": {
        "url": "http://nocodb:8080/api/v2/tables/Tasks/records",
        "authentication": "genericCredentialType",
        "genericAuthType": "httpHeaderAuth",
        "options": {
          "qs": {
            "parameter": [
              {
                "name": "where",
                "value": "(Status,eq,In Progress)"
              }
            ]
          }
        }
      },
      "id": "get-in-progress",
      "name": "Get In Progress",
      "type": "n8n-nodes-base.httpRequest",
      "typeVersion": 4.1,
      "position": [460, 460],
