input.command:
mkdir -p ~/buildrightpros-template/{src/{components/{core,basic,premium},layouts,pages},public} && cd ~/buildrightpros-template && cat > package.json << 'EOF'
{
  "name": "buildrightpros-template",
  "type": "module",
  "scripts": {
    "dev": "astro dev",
    "build": "astro build",
    "preview": "astro preview"
  },
  "dependencies": {
    "astro": "^4.0.0",
    "@astrojs/tailwind": "^5.0.0",
    "tailwindcss": "^3.4.0"
  }
}
EOF
echo "package.json created"

input.content:
export default {
  content: ['./src/**/*.{astro,html,js}'],
  theme: {
    extend: {
      fontFamily: {
        header: ['"Titan One"', '"Arial Black"', 'sans-serif'],
        body: ['Trebuchet', 'Montserrat', 'Arial', 'sans-serif']
      },
      colors: {
        brand: { light: '#f5f5f5', dark: '#1a1a1a', accent: '#3b82f6' }
      }
    }
  }
}

import { defineConfig } from 'astro/config';
import tailwind from '@astrojs/tailwind';
export default defineConfig({ integrations: [tailwind()] });

